import React, {Component} from "react";
import RecordList from "./components/recordList";
import {Router} from "@reach/router";

class App extends Component {
API_URL = process.env.REACT_APP_API_URL;

  constructor(props) {
    super(props);
    this.state = {
      recordlist: []
    };
}

componentDidMount() {
    // Get everything from the API
    this.getRecordList().then(() => console.log("RecordList got it!"));
}
  async getRecordList() {
      const url = `${this.API_URL}/recordList`;
      let result = await fetch(url); // Get the data
      let json = await result.json(); // Turn it into json
      return this.setState({ // Set it in the state
        recordlist: json
    })
  }


  render() {
    return (
        <>
            <Router>
                <recordList path="/" recordlist={this.state.recordlist}/>
            </Router>
        </>
    );
}
}

export default App;
