import React, { Component } from "react";
// This will require to npm install axios
import axios from 'axios';
//import { Link } from "react-router-dom";

const Record = (props) => (
  <tr>
    <td>{props.record.article_title}</td>
    <td>{props.record.article_description}</td>
    <td>

    </td>
  </tr>
);

class App extends Component {
  // API url from the file '.env' OR the file '.env.development'.
  // The first file is only used in production.
  API_URL = process.env.REACT_APP_API_URL;

  constructor(props) {
      super(props);
      this.state = {
          //artwalks: [],

          records: []
      };
  }

  componentDidMount() {
      // Get everything from the API

      this.getRecords().then(() => console.log("Records got it!"));
  }

async getRecords() {
      let url = `${this.API_URL}/records`; // URL of the API.
      let result = await fetch(url); // Get the data
      let json = await result.json(); // Turn it into json
      return this.setState({ // Set it in the state
          records: json
      })

  }

  getRecord(id) {
      // Find the relevant artwalk by id
      return this.state.records.find(k => k._id === id);
  }


  // This method will map out the users on the table
  recordList() {
    return this.state.records.map((currentrecord) => {
      return (
        <Record
          record={currentrecord}
          key={currentrecord._id}
        />
      );
    });
  }

  // This following section will display the table with the records of individuals.
  render() {
    return (
      <div style={{paddingBottom:"50px"}} className="container">
        <table className="table table-striped" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Title</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>{this.recordList()}</tbody>
        </table>
      </div>
    );
  }
}
